import { StyleSheet, Text, View, Image, TouchableOpacity, SafeAreaView, Dimensions, TextInput, ImageBackground } from 'react-native';

export default function LoginScreen() {
    return (
        <SafeAreaView style={styles.container}>
            <ImageBackground
                source={require('../assets/backgroundLogin.png')}
                style={{width: '100%', height: '100%', flex: 1, alignItems: 'center', justifyContent: 'space-between'}}
                resizeMode='cover'
            >
                <View></View>
                <View style={styles.containerBottom}>
                    <TouchableOpacity
                        style={[styles.btn, {backgroundColor: '#009ef9'}]}
                    >
                        <Text style={{fontSize: 20, fontWeight: '500', color: '#fff'}}>Đăng nhập</Text>
                    </TouchableOpacity>
                    <TouchableOpacity></TouchableOpacity>
                </View>
            </ImageBackground>
            
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      alignItems: 'center',
      justifyContent: 'space-between',
    //   backgroundColor: '#009ef9',
    },
    containerBottom: {
        width: '80%',
    },
    btn: {
        width: '100%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 30,
    }
  });
